#!/bin/bash

TARGET=$1

if [ "$TARGET" == "" ]  || [ ! -f $TARGET ]; then
	exit 1
fi

while read FILENAME TMP; do
	FRAMEWORK=${FILENAME##*lib/}
	install_name_tool -change $FILENAME @loader_path/../../node_modules/qt-darwin/Frameworks/${FRAMEWORK} $TARGET
done <<EOF
$(otool -L $TARGET | grep ".framework")
EOF
